}

.center {
    display: flex;
    justify-content: center;
    align-items: center;
}

.nav-menu {
    list-style: none;
    display: flex;
    justify-content: space-between;
}

.nav-menu li {
    margin-right: 10px;
}

.nav-menu a {
    text-decoration: none;
    color: #333;
}

.button {
    display: block;
    width: 100%;
    padding: 10px 20px;
    background-color: #333;
    color: #fff;
    text-decoration: none;
    border-radius: 4px;
    cursor: pointer;
}

img {
    max-width: 100%;
    height: auto;
}

@media (max-width: 768px) {
    img {
        max-width: 50%;
    }
}

.nav {
    display: flex;
    justify-content: space-between;
}

@media (max-width: 768px) {
    .nav {
        flex-direction: column;
    }
}

div {
    display: block;
    display: inline-block;
    display: inline;
    display: none;
}

.element {
    border-top: 1px solid blue;
    border-right: 2px dashed green;
    border-bottom: 3px dotted red;
    border-left: 4px double orange;
}

.element {
    font-family: 'Proxima Nova', Helvetica, Arial, sans-serif;
    font-style: italic;
    font-variant: small-caps;
    font-size: 20px;
    font-weight: bold;
    line-height: 1.5;
}

@keyframes typing {
    from { width: 0; }
    to { width: 100%; }
}
.element {
    overflow: hidden;
    white-space: nowrap;
    animation: typing 5s steps(20) infinite;
}
